reference_sva:
property p_cop_irq_assignment_on_reset;
    @(posedge bus_clk) disable iff (async_rst_b == 1)
        async_rst_b == 0 |-> ##1 cop_irq == 0;
endproperty
assert_p_cop_irq_assignment_on_reset: assert property (p_cop_irq_assignment_on_reset) else $error("Assertion failed: cop_irq is not 0 one cycle after async_rst_b is deasserted.");
