reference_sva:
property p_fIMM_assignment;
    @(posedge gclk) disable iff (grst == 1)
        rOPC == 6'o54 |-> fIMM == 1;
endproperty
assert_p_fIMM_assignment: assert property (p_fIMM_assignment) else $error("Assertion failed: When rOPC is 6'o54, fIMM should be 1");

generated_sva:
property p_rOPC_fIMM_check;
  @(posedge gclk) disable iff (grst)
  (rOPC == 'o54) |-> (fIMM == 1'b1);
endproperty

assert_rOPC_fIMM_check: assert property (p_rOPC_fIMM_check);
