reference_sva:
property p_broadcast_counter_reset;
    @(posedge Clk) disable iff (Reset == 0)
        Reset == 1 |-> ##1 broadcast_counter == 0;
endproperty
assert_p_broadcast_counter_reset: assert property (p_broadcast_counter_reset) else $error("Assertion failed: broadcast_counter is not reset to 0 one cycle after Reset is asserted");
