reference_sva:
property p_ZeroLeft_assignment_on_reset;
    @(posedge clk) disable iff (rst_n == 1)
        rst_n == 0 |-> ##1 ZeroLeft == 0;
endproperty
assert_p_ZeroLeft_assignment_on_reset: assert property (p_ZeroLeft_assignment_on_reset) else $error("Assertion failed: ZeroLeft signal is not assigned to 0 one cycle after reset is deasserted");
