reference_sva:
property p_synchro_reset;
    @(posedge clk_i) disable iff (rst_i == 0)
        rst_i == 1 |-> ##1 synchro == 3'b0;
endproperty
assert_p_synchro_reset: assert property (p_synchro_reset) else $error("Assertion failed: synchro signal is not 3'b0 one cycle after reset is asserted");

generated_sva:
property p_synchro_reset_check;
  @(posedge clk_i) disable iff (rst_i == 1'b0)
  $rose(rst_i) |=> (synchro == 3'b0);
endproperty

assert property (p_synchro_reset_check)
  else $error("synchro signal did not become zero one cycle after reset was asserted");
