reference_sva:
property p_sync_reset_assignment;
    @(posedge wb_clk_i) disable iff (wb_rst_i == 0)
        wb_rst_i == 1 |-> sync_reset == wb_rst_i;
endproperty
assert_p_sync_reset_assignment: assert property (p_sync_reset_assignment) else $error("Assertion failed: sync_reset signal does not match wb_rst_i when wb_rst_i is asserted");
