reference_sva:
property p_ReceivedPauseFrmWAddr_reset;
    @(posedge MRxClk) disable iff (RxReset==0)
        (RxReset == 1) |-> ##1 (ReceivedPauseFrmWAddr == 0);
endproperty
assert_p_ReceivedPauseFrmWAddr_reset: assert property (p_ReceivedPauseFrmWAddr_reset) else $error("Assertion failed: ReceivedPauseFrmWAddr is not 0 one cycle after RxReset is asserted");
