reference_sva:
property p_bready_reset_logic;
    @(posedge M_AXI_ACLK) disable iff (M_AXI_ARESETN==1)
        (M_AXI_ARESETN == 0) |-> ##1 (bready == 0);
endproperty
assert_p_bready_reset_logic: assert property (p_bready_reset_logic) else $error("Assertion failed: bready signal is not 0 one cycle after reset is asserted");

generated_sva:
property bready_after_reset;
    @(posedge M_AXI_ACLK) disable iff (M_AXI_ARESETN)
    !M_AXI_ARESETN |=> !bready;
endproperty

assert property (bready_after_reset);
