reference_sva:
property p_LatchedCrcError_Reset;
    @(posedge MRxClk) disable iff (Reset == 0)
        Reset == 1 |-> ##1 LatchedCrcError == 0;
endproperty
assert_p_LatchedCrcError_Reset: assert property (p_LatchedCrcError_Reset) else $error("Assertion failed: LatchedCrcError should be 0 one cycle after Reset is asserted");

generated_sva:
property p_latched_crc_error_cleared_after_reset;
  @(posedge MRxClk) disable iff (!Reset)
  $rose(Reset) |=> (LatchedCrcError == 1'b0);
endproperty

assert property (p_latched_crc_error_cleared_after_reset)
  else $error("LatchedCrcError was not cleared one cycle after Reset became active");
