reference_sva:
property p_wr_pointer_increment;
    @(posedge wr_clk)
        (i_push == 1 && o_full == 0 && wr_pointer < '1) |-> ##1 wr_pointer == $past(wr_pointer) + 1'd1;
endproperty
assert_wr_pointer_increment: assert property (p_wr_pointer_increment) else $error("Assertion failed: wr_pointer did not increment correctly when i_push is asserted and o_full is deasserted.");

generated_sva:
property prop_wr_pointer_increment;
  @(posedge wr_clk)
  (i_push && !o_full && (wr_pointer < '1)) |=> (wr_pointer == $past(wr_pointer) + 1);
endproperty

assert_wr_pointer_increment: assert property (prop_wr_pointer_increment);
