reference_sva:
property p_wr_vld_assignment;
    @(posedge clk) disable iff (reset_l == 0)
        (wr_en == 1 && rst_tri_en == 0 && reset_l == 1) |-> (wr_vld == 1);
endproperty
assert_p_wr_vld_assignment: assert property (p_wr_vld_assignment) else $error("Assertion failed: wr_vld signal is not asserted when wr_en is enabled, rst_tri_en is disabled, and reset_l is active");

generated_sva:
property wr_vld_assertion;
  @(posedge clk) disable iff (!reset_l)
  (wr_en && !rst_tri_en && reset_l) |-> wr_vld;
endproperty

assert property (wr_vld_assertion);
