reference_sva:
property p_o_quotient_assignment;
    @(posedge i_clk) disable iff (i_rst)
        (r_busy == 1 && r_divisor[(2*BW-2):(BW)] == 0 && diff[BW] == 0) |-> ##1 o_quotient == { $past(o_quotient[(BW-2):0]), 1'b1 };
endproperty
assert_p_o_quotient_assignment: assert property (p_o_quotient_assignment) else $error("Assertion failed: o_quotient is not assigned correctly when r_busy is 1, r_divisor[(2*BW-2):(BW)] is 0, and diff[BW] is 0");
