reference_sva:
property p_A_assignment_on_reset;
    @(posedge Clk) disable iff (Rst == 0)
        Rst == 1 |-> ##1 A == 0;
endproperty
assert_p_A_assignment_on_reset: assert property (p_A_assignment_on_reset) else $error("Assertion failed: A is not assigned to 0 one cycle after reset is asserted");

generated_sva:
property p_reset_initializes_A;
  @(posedge Clk) disable iff (!Rst)
  $rose(Rst) |=> (A == 0);
endproperty

assert property (p_reset_initializes_A)
  else $error("Signal A was not assigned to 0 one clock cycle after reset assertion");
